def calculate_brightness(img):
    if not img:
        return -1
    height = len(img)
    width = len(img[0]) if height > 0 else 0
    total_pixels = height * width
    brightness_sum = 0
    for row in img:
        if len(row) != width:
            return -1
        for pixel in row:
            if pixel < 0 or pixel > 255:
                return -1
            brightness_sum += pixel
    if total_pixels == 0:
        return -1
    average_brightness = brightness_sum / total_pixels
    return round(average_brightness, 2)
