def solve_jacobi(A: np.ndarray, b: np.ndarray, n: int) -> list:
    x = np.zeros_like(b)
    for _ in range(n):
        x_new = np.zeros_like(x)
        for i in range(A.shape[0]):
            s = 0
            for j in range(A.shape[1]):
                if i != j:
                    s += A[i, j] * x[j]
            x_new[i] = (b[i] - s) / A[i, i]
        x = np.round(x_new, 4)
    return x.tolist()
