def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    (n_samples, n_features) = data.shape
    alpha = np.zeros(n_samples)
    bias = 0

    def linear_kernel(x1, x2):
        return np.dot(x1, x2.T)

    def rbf_kernel(x1, x2, sigma):
        dist = np.linalg.norm(x1[:, np.newaxis] - x2, axis=2)
        return np.exp(-dist ** 2 / (2 * sigma ** 2))
    if kernel == 'linear':
        kernel_func = linear_kernel
    elif kernel == 'RBF':
        kernel_func = lambda x1, x2: rbf_kernel(x1, x2, sigma)
    else:
        raise ValueError("Invalid kernel type. Choose 'linear' or 'RBF'.")
    for t in range(1, iterations + 1):
        eta = 1 / (lambda_val * t)
        kernel_matrix = kernel_func(data, data)
        scores = np.dot(alpha * labels, kernel_matrix) + bias
        hinge_loss_indicator = labels * scores < 1
        alpha = (1 - eta * lambda_val) * alpha
        alpha[hinge_loss_indicator] += eta * labels[hinge_loss_indicator]
        bias += eta * np.sum(labels[hinge_loss_indicator] - scores[hinge_loss_indicator])
    alpha = np.round(alpha, 4)
    bias = np.round(bias, 4)
    return (alpha.tolist(), [bias])
