def polynomial_features(X, degree):
    (n_samples, n_features) = X.shape
    feature_combinations = []
    for d in range(1, degree + 1):
        combos = combinations_with_replacement(range(n_features), d)
        for combo in combos:
            feature_combinations.append(combo)
    n_new_features = len(feature_combinations)
    X_poly = np.empty((n_samples, n_new_features))
    for i in range(n_samples):
        for (j, combo) in enumerate(feature_combinations):
            X_poly[i, j] = np.prod(X[i, combo])
    return X_poly.tolist()
