class Optimizer:

    def __init__(self):
        pass

    def update(self, w, grad_wrt_w):
        return w - 0.01 * grad_wrt_w
class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        self.layer_input = None
        self.input_shape = input_shape
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.optimizer_W = Optimizer()
        self.optimizer_w0 = Optimizer()

    def initialize(self):
        limit = 1 / math.sqrt(self.input_shape[0])
        self.W = np.random.uniform(-limit, limit, (self.input_shape[0], self.n_units))
        self.w0 = np.zeros((1, self.n_units))

    def parameters(self):
        return np.prod(self.W.shape) + np.prod(self.w0.shape)

    def forward_pass(self, X, training):
        self.layer_input = X
        return np.dot(X, self.W) + self.w0

    def backward_pass(self, accum_grad):
        grad_wrt_W = np.dot(self.layer_input.T, accum_grad)
        grad_wrt_w0 = np.sum(accum_grad, axis=0, keepdims=True)
        if self.trainable:
            self.W = self.optimizer_W.update(self.W, grad_wrt_W)
            self.w0 = self.optimizer_w0.update(self.w0, grad_wrt_w0)
        return np.dot(accum_grad, self.W.T)

    def output_shape(self):
        return (self.n_units,)
