def compute_tf_idf(corpus, query):
    if not corpus:
        return []
    num_docs = len(corpus)
    tf_idf_scores = []
    for doc in corpus:
        doc_scores = []
        for term in query:
            if len(doc) == 0:
                tf = 0
            else:
                tf = doc.count(term) / len(doc)
            df = sum((1 for d in corpus if term in d))
            idf = np.log((num_docs + 1) / (df + 1)) + 1
            tf_idf = tf * idf
            doc_scores.append(round(tf_idf, 4))
        tf_idf_scores.append(doc_scores)
    return np.array(tf_idf_scores).tolist()
