def performance_metrics(actual: list[int], predicted: list[int]) -> tuple:
    if len(actual) != len(predicted):
        raise ValueError('The actual and predicted lists must have the same length.')
    for i in range(len(actual)):
        if actual[i] not in [0, 1] or predicted[i] not in [0, 1]:
            raise ValueError('All elements in the actual and predicted lists must be either 0 or 1.')
    tp = 0
    fp = 0
    tn = 0
    fn = 0
    for i in range(len(actual)):
        if actual[i] == 1 and predicted[i] == 1:
            tp += 1
        elif actual[i] == 0 and predicted[i] == 1:
            fp += 1
        elif actual[i] == 0 and predicted[i] == 0:
            tn += 1
        elif actual[i] == 1 and predicted[i] == 0:
            fn += 1
    confusion_matrix = [[tn, fp], [fn, tp]]
    accuracy = (tp + tn) / (tp + tn + fp + fn)
    if tp + fn == 0 or tp + fp == 0:
        f1_score = 0
    else:
        precision = tp / (tp + fp)
        recall = tp / (tp + fn)
        f1_score = 2 * (precision * recall) / (precision + recall)
    if tn + fp == 0:
        specificity = 0
    else:
        specificity = tn / (tn + fp)
    if tn + fn == 0:
        negative_predictive_value = 0
    else:
        negative_predictive_value = tn / (tn + fn)
    accuracy = round(accuracy, 3)
    f1_score = round(f1_score, 3)
    specificity = round(specificity, 3)
    negative_predictive_value = round(negative_predictive_value, 3)
    return (confusion_matrix, accuracy, f1_score, specificity, negative_predictive_value)
