def layer_norm(x, g, b, eps: float=1e-05):
    mean = np.mean(x, axis=-1, keepdims=True)
    variance = np.var(x, axis=-1, keepdims=True)
    return g * (x - mean) / np.sqrt(variance + eps) + b
def multi_head_attention(x, params, hparams):
    return x
def feed_forward(x, params):
    return x
def gen_text(prompt: str, n_tokens_to_generate: int=40):
    (encoder, hparams, params) = load_encoder_hparams_and_params()
    input_ids = encoder.encode(prompt)
    for _ in range(n_tokens_to_generate):
        token_embeds = params['wte'][input_ids]
        position_ids = np.arange(len(input_ids))
        position_embeds = params['wpe'][position_ids]
        x = token_embeds + position_embeds
        x = multi_head_attention(x, params, hparams)
        x = feed_forward(x, params)
        x = layer_norm(x, params['ln_f']['g'], params['ln_f']['b'])
        next_token = np.random.randint(0, 3)
        input_ids.append(next_token)
    generated_text = encoder.decode(input_ids)
    return generated_text
