def update_header_table(item, target_node, header_table):
    if header_table[item][1] is None:
        header_table[item][1] = target_node
    else:
        current_node = header_table[item][1]
        while current_node.node_link is not None:
            current_node = current_node.node_link
        current_node.node_link = target_node


def update_tree(items, root, header_table):
    if items[0] in root.children:
        root.children[items[0]].increment(1)
    else:
        root.children[items[0]] = TreeNode(items[0], 1, root)
        if header_table[items[0]][1] is None:
            header_table[items[0]][1] = root.children[items[0]]
        else:
            update_header_table(items[0], root.children[items[0]], header_table)
    if len(items) > 1:
        update_tree(items[1:], root.children[items[0]], header_table)


def create_tree(transactions, min_support):
    item_counts = Counter()
    for transaction in transactions:
        for item in transaction:
            item_counts[item] += 1
    frequent_items = {item: count for item, count in item_counts.items() if count >= min_support}
    if len(frequent_items) == 0:
        return None, None
    header_table = defaultdict(lambda: [0, None])
    for item, count in frequent_items.items():
        header_table[item][0] = count
    root = TreeNode('Null', 0, None)
    for transaction in transactions:
        filtered_items = [item for item in transaction if item in frequent_items]
        if len(filtered_items) > 0:
            sorted_items = sorted(filtered_items, key=lambda x: frequent_items[x], reverse=True)
            update_tree(sorted_items, root, header_table)
    return root, header_table


def ascend_tree(node):
    path = []
    while node and node.item != 'Null':
        path.append(node.item)
        node = node.parent
    return path


def find_prefix_path(base_pattern, header_table):
    tree_nodes = []
    node = header_table[base_pattern][1]
    while node is not None:
        prefix_path = ascend_tree(node)
        if len(prefix_path) > 1:
            tree_nodes.append(prefix_path[1:])
        node = node.node_link
    return tree_nodes


def mine_tree(header_table, min_support, prefix, frequent_itemsets):
    sorted_items = sorted(header_table.items(), key=lambda p: p[1][0])
    for base_pattern, _ in sorted_items:
        new_prefix = prefix.copy()
        new_prefix.append(base_pattern)
        frequent_itemsets.append(sorted(new_prefix))
        conditional_pattern_bases = find_prefix_path(base_pattern, header_table)
        conditional_tree, conditional_header_table = create_tree(conditional_pattern_bases, min_support)
        if conditional_header_table is not None:
            mine_tree(conditional_header_table, min_support, new_prefix, frequent_itemsets)


def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    # Write your code below
    root, header_table = create_tree(transactions, min_support)
    if root is None:
        return []
    frequent_itemsets = []
    mine_tree(header_table, min_support, [], frequent_itemsets)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets

