import numpy as np


def decision_tree_predict(
    X_train: np.ndarray,
    y_train: np.ndarray,
    X_test: np.ndarray,
    max_depth: int | None = None,
) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    def gini_impurity(y):
        if len(y) == 0:
            return 0
        classes, counts = np.unique(y, return_counts=True)
        probabilities = counts / len(y)
        return 1 - np.sum(probabilities ** 2)

    def gini_gain(parent, left, right):
        n = len(parent)
        if n == 0:
            return 0
        p_left = len(left) / n
        p_right = len(right) / n
        return gini_impurity(parent) - p_left * gini_impurity(left) - p_right * gini_impurity(right)

    def find_best_split(X, y):
        best_gain = 0
        best_feature = None
        best_threshold = None
        n_features = X.shape[1]
        for feature in range(n_features):
            feature_values = X[:, feature]
            sorted_indices = np.argsort(feature_values)
            sorted_y = y[sorted_indices]
            sorted_feature = feature_values[sorted_indices]
            for i in range(1, len(sorted_y)):
                if sorted_feature[i] != sorted_feature[i - 1]:
                    threshold = (sorted_feature[i] + sorted_feature[i - 1]) / 2
                    left_indices = feature_values < threshold
                    left_y = y[left_indices]
                    right_y = y[~left_indices]
                    gain = gini_gain(y, left_y, right_y)
                    if gain > best_gain:
                        best_gain = gain
                        best_feature = feature
                        best_threshold = threshold
        return best_gain, best_feature, best_threshold

    def build_tree(X, y, depth=0):
        if len(np.unique(y)) == 1:
            return np.unique(y)[0]
        if max_depth is not None and depth == max_depth:
            return np.bincount(y).argmax()
        gain, feature, threshold = find_best_split(X, y)
        if gain == 0:
            return np.bincount(y).argmax()
        left_indices = X[:, feature] < threshold
        X_left, y_left = X[left_indices], y[left_indices]
        X_right, y_right = X[~left_indices], y[~left_indices]
        if len(y_left) == 0 or len(y_right) == 0:
            return np.bincount(y).argmax()
        return {
            'feature': feature,
            'threshold': threshold,
            'left': build_tree(X_left, y_left, depth + 1),
            'right': build_tree(X_right, y_right, depth + 1)
        }

    def predict_sample(tree, sample):
        if not isinstance(tree, dict):
            return tree
        if sample[tree['feature']] < tree['threshold']:
            return predict_sample(tree['left'], sample)
        else:
            return predict_sample(tree['right'], sample)

    tree = build_tree(X_train, y_train)
    predictions = [predict_sample(tree, sample) for sample in X_test]
    return predictions


