import numpy as np
import math


def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    n_samples, n_features = data.shape
    if sample_size >= n_samples:
        sample_size = n_samples

    def c(n):
        if n == 1:
            return 0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples

    class Node:
        def __init__(self, left=None, right=None, split_feature=None, split_threshold=None, samples=None):
            self.left = left
            self.right = right
            self.split_feature = split_feature
            self.split_threshold = split_threshold
            self.samples = samples

    def build_tree(subsample, depth):
        height_limit = math.ceil(math.log2(sample_size))
        if depth >= height_limit or len(subsample) <= 1:
            return Node(samples=subsample)
        split_feature = np.random.randint(0, n_features)
        min_val = np.min(subsample[:, split_feature])
        max_val = np.max(subsample[:, split_feature])
        split_threshold = np.random.uniform(min_val, max_val)
        left_subsample = subsample[subsample[:, split_feature] < split_threshold]
        right_subsample = subsample[subsample[:, split_feature] >= split_threshold]
        left_node = build_tree(left_subsample, depth + 1)
        right_node = build_tree(right_subsample, depth + 1)
        return Node(left=left_node, right=right_node, split_feature=split_feature, split_threshold=split_threshold)

    def path_length(x, node, depth):
        if node.samples is not None:
            return depth + c(len(node.samples))
        if x[node.split_feature] < node.split_threshold:
            return path_length(x, node.left, depth + 1)
        return path_length(x, node.right, depth + 1)

    trees = []
    for _ in range(n_trees):
        indices = np.random.choice(n_samples, sample_size, replace=False)
        subsample = data[indices]
        tree = build_tree(subsample, 0)
        trees.append(tree)

    scores = []
    for x in data:
        total_path_length = 0
        for tree in trees:
            total_path_length += path_length(x, tree, 0)
        avg_path_length = total_path_length / n_trees
        score = 2 ** (-avg_path_length / phi)
        scores.append(score)

    if epsilon == 0:
        return []
    num_outliers = int(epsilon * n_samples)
    outlier_indices = np.argsort(scores)[-num_outliers:]
    outlier_indices.sort()
    return outlier_indices.tolist()


