def calculate_covariance_matrix(vectors: list[list[float]]) -> list[list[float]]:
    if not vectors:
        raise ValueError('Input list is empty.')
    n = len(vectors[0])
    if n < 2:
        raise ValueError('Each feature must have at least 2 observations.')
    for vec in vectors:
        if len(vec) != n:
            raise ValueError('All vectors must have the same length.')
    d = len(vectors)
    means = [sum(vec) / n for vec in vectors]
    covariance_matrix = []
    for i in range(d):
        row = []
        for j in range(d):
            sum_products = 0.0
            for (x, y) in zip(vectors[i], vectors[j]):
                sum_products += (x - means[i]) * (y - means[j])
            covariance = sum_products / (n - 1)
            row.append(covariance)
        covariance_matrix.append(row)
    return covariance_matrix

