class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        self.layer_input = None
        self.input_shape = input_shape
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.optimizer_W = None
        self.optimizer_w0 = None

    def initialize(self, optimizer):
        """Initialize weights and optimizers."""
        input_dim = self.input_shape[0]
        limit = 1 / math.sqrt(input_dim)
        self.W = np.random.uniform(-limit, limit, (input_dim, self.n_units))
        self.w0 = np.zeros(self.n_units)
        self.optimizer_W = copy.copy(optimizer)
        self.optimizer_w0 = copy.copy(optimizer)

    def parameters(self):
        """Return total number of trainable parameters."""
        if self.W is None or self.w0 is None:
            return 0
        return np.prod(self.W.shape) + np.prod(self.w0.shape)

    def forward_pass(self, X, training=True):
        """Compute forward pass: X.dot(W) + w0."""
        self.layer_input = X
        return np.dot(X, self.W) + self.w0

    def backward_pass(self, accum_grad):
        """Compute gradient w.r.t. input and update parameters if trainable."""
        dX = np.dot(accum_grad, self.W.T)
        if self.trainable:
            dW = np.dot(self.layer_input.T, accum_grad)
            dw0 = np.sum(accum_grad, axis=0)
            self.W = self.optimizer_W.update(self.W, dW)
            self.w0 = self.optimizer_w0.update(self.w0, dw0)
        return dX

    def output_shape(self):
        """Return output shape (n_units,)."""
        return (self.n_units,)

