def gauss_seidel(A, b, n, x_ini=None):
    A = np.array(A, dtype=float)
    b = np.array(b, dtype=float)
    m = A.shape[0]
    if x_ini is None:
        x = np.zeros(m, dtype=float)
    else:
        x = np.array(x_ini, dtype=float)
    for _ in range(n):
        for i in range(m):
            sum_updated = np.dot(A[i, :i], x[:i])
            sum_old = np.dot(A[i, i + 1:], x[i + 1:])
            x[i] = (b[i] - sum_updated - sum_old) / A[i, i]
    x_rounded = np.round(x, decimals=4)
    return x_rounded.tolist()

