def gaussian_elimination(A, b):
    A = np.array(A, dtype=float)
    b = np.array(b, dtype=float)
    n = A.shape[0]
    augmented = np.hstack((A, b.reshape(-1, 1)))
    for pivot in range(n):
        max_row = np.argmax(np.abs(augmented[pivot:, pivot])) + pivot
        augmented[[pivot, max_row]] = augmented[[max_row, pivot]]
        if np.abs(augmented[pivot, pivot]) < 1e-10:
            raise ValueError('Matrix is singular or nearly singular')
        for i in range(pivot + 1, n):
            factor = augmented[i, pivot] / augmented[pivot, pivot]
            augmented[i, :] -= factor * augmented[pivot, :]
    x = np.zeros(n)
    for i in range(n - 1, -1, -1):
        sum_terms = np.sum(augmented[i, i + 1:n] * x[i + 1:n])
        x[i] = (augmented[i, n] - sum_terms) / augmented[i, i]
    return np.round(x, 4).tolist()

import numpy as np
def compute_tf_idf(corpus, query):
    if not corpus:
        return []
    N = len(corpus)
    doc_word_counts = []
    doc_lengths = []
    for doc in corpus:
        word_counts = {}
        for word in doc:
            word_counts[word] = word_counts.get(word, 0) + 1
        doc_word_counts.append(word_counts)
        doc_lengths.append(len(doc))
    idf_scores = []
    for term in query:
        doc_freq = 0
        for counts in doc_word_counts:
            if term in counts:
                doc_freq += 1
        idf = np.log((N + 1) / (doc_freq + 1))
        idf_scores.append(idf)
    result = []
    for doc_idx in range(N):
        doc_tf_idf = []
        doc_len = doc_lengths[doc_idx]
        doc_counts = doc_word_counts[doc_idx]
        for (term_idx, term) in enumerate(query):
            if doc_len == 0:
                tf = 0.0
            else:
                term_count = doc_counts.get(term, 0)
                tf = term_count / doc_len
            tf_idf = tf * idf_scores[term_idx]
            doc_tf_idf.append(round(tf_idf, 5))
        result.append(doc_tf_idf)
    return np.array(result).tolist()

def calculate_brightness(img):
    total_pixels = sum((len(row) for row in img))
    if total_pixels == 0:
        return -1
    row_length = len(img[0]) if img else 0
    for row in img:
        if len(row) != row_length:
            return -1
    for row in img:
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
    sum_pixels = sum((pixel for row in img for pixel in row))
    average = sum_pixels / total_pixels
    return round(average, 2)

