def cramers_rule(A, b):
    if A.shape[0] != A.shape[1]:
        return -1
    n = A.shape[0]
    if len(b) != n:
        return -1
    det_A = np.linalg.det(A)
    if np.isclose(det_A, 0, atol=1e-10):
        return -1
    x = np.zeros(n)
    for i in range(n):
        A_i = A.copy()
        A_i[:, i] = b
        det_Ai = np.linalg.det(A_i)
        x[i] = det_Ai / det_A
    return np.round(x, 4).tolist()

