import numpy as np
def group_normalization(X: np.ndarray, gamma: np.ndarray, beta: np.ndarray, num_groups: int, epsilon: float=1e-05):
    if X.ndim != 4:
        raise ValueError('Input X must be a 4D tensor with shape (B, C, H, W)')
    (B, C, H, W) = X.shape
    if C % num_groups != 0:
        raise ValueError(f'num_groups ({num_groups}) must divide the number of channels ({C})')
    if gamma.shape != (C,) or beta.shape != (C,):
        raise ValueError(f'gamma and beta must have shape ({C},), got {gamma.shape} and {beta.shape}')
    G = C // num_groups
    X_grouped = X.reshape(B, num_groups, G, H, W)
    mean = np.mean(X_grouped, axis=(2, 3, 4), keepdims=True)
    var = np.var(X_grouped, axis=(2, 3, 4), keepdims=True)
    normalized_grouped = (X_grouped - mean) / np.sqrt(var + epsilon)
    normalized = normalized_grouped.reshape(B, C, H, W)
    gamma_reshaped = gamma.reshape(1, C, 1, 1)
    beta_reshaped = beta.reshape(1, C, 1, 1)
    output = normalized * gamma_reshaped + beta_reshaped
    return np.round(output, decimals=4).tolist()

