from collections import Counter
def entropy(examples: list[dict], target_attr: str) -> float:
    """Calculate the entropy of the target attribute in the given examples."""
    label_counts = Counter((example[target_attr] for example in examples))
    total_examples = len(examples)
    return -sum((count / total_examples * math.log2(count / total_examples) for count in label_counts.values() if count > 0))
def information_gain(examples: list[dict], attribute: str, target_attr: str) -> float:
    """Calculate the information gain of a given attribute."""
    total_entropy = entropy(examples, target_attr)
    attribute_values = Counter((example[attribute] for example in examples))
    weighted_entropy = 0.0
    for (value, count) in attribute_values.items():
        subset = [example for example in examples if example[attribute] == value]
        weighted_entropy += count / len(examples) * entropy(subset, target_attr)
    return total_entropy - weighted_entropy
def best_attribute(examples: list[dict], attributes: list[str], target_attr: str) -> str:
    """Find the attribute with the highest information gain."""
    gains = {attr: information_gain(examples, attr, target_attr) for attr in attributes}
    return max(gains, key=gains.get)
def majority_class(examples: list[dict], target_attr: str) -> str:
    """Return the majority class from the examples."""
    return Counter((example[target_attr] for example in examples)).most_common(1)[0][0]
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    """Learn a decision tree from the given examples and attributes."""
    if not examples:
        return {}
    if len(set((example[target_attr] for example in examples))) == 1:
        return examples[0][target_attr]
    if not attributes:
        return majority_class(examples, target_attr)
    best_attr = best_attribute(examples, attributes, target_attr)
    tree = {best_attr: {}}
    best_attr_values = set((example[best_attr] for example in examples))
    for value in best_attr_values:
        subset = [example for example in examples if example[best_attr] == value]
        subtree = learn_decision_tree(subset, [attr for attr in attributes if attr != best_attr], target_attr)
        tree[best_attr][value] = subtree
    return tree
