import numpy as np
def sigmoid(x):
    return 1 / (1 + np.exp(-x))
def sigmoid_derivative(x):
    return x * (1 - x)
def train_neuron(features: np.ndarray, labels: np.ndarray, initial_weights: np.ndarray, initial_bias: float, learning_rate: float, epochs: int) -> (np.ndarray, float, list[float]):
    weights = initial_weights
    bias = initial_bias
    mse_values = []
    for epoch in range(epochs):
        linear_output = np.dot(features, weights) + bias
        predictions = sigmoid(linear_output)
        mse = np.mean((predictions - labels) ** 2)
        mse_values.append(round(mse, 4))
        error = predictions - labels
        d_predictions = error * sigmoid_derivative(predictions)
        weights -= learning_rate * np.dot(features.T, d_predictions) / len(labels)
        bias -= learning_rate * np.mean(d_predictions)
    return (weights, round(bias, 4), mse_values)
