def compute_tf_idf(corpus, query):
    if not corpus:
        raise ValueError('Empty corpus provided')
    tf_matrix = []
    for doc in corpus:
        tf_doc = {}
        total_words = len(doc)
        for word in doc:
            tf_doc[word] = tf_doc.get(word, 0) + 1 / total_words
        tf_matrix.append(tf_doc)
    idf_dict = {}
    for word in query:
        df = sum((1 for doc in corpus if word in doc))
        idf_dict[word] = np.log((len(corpus) + 1) / (df + 1)) + 1
    tf_idf_matrix = []
    for tf_doc in tf_matrix:
        tf_idf_doc = []
        for word in query:
            tf_idf_score = tf_doc.get(word, 0) * idf_dict[word]
            tf_idf_doc.append(round(tf_idf_score, 5))
        tf_idf_matrix.append(tf_idf_doc)
    return np.array(tf_idf_matrix).tolist()
