def pattern_weaver(n, crystal_values, dimension):

    def softmax(values):
        exp_values = np.exp(values - np.max(values))
        return exp_values / exp_values.sum(axis=0)
    crystal_values = np.array(crystal_values)
    attention_scores = np.zeros((n, n))
    for i in range(n):
        for j in range(n):
            if i != j:
                attention_scores[i][j] = crystal_values[i] * crystal_values[j]
    attention_weights = np.zeros_like(attention_scores)
    for i in range(n):
        attention_weights[i] = softmax(attention_scores[i])
    weighted_patterns = np.zeros(n)
    for i in range(n):
        weighted_patterns[i] = np.dot(attention_weights[i], crystal_values)
    return np.round(weighted_patterns.tolist(), 4)
