import numpy as np
def train_decision_stump(X: np.ndarray, y: np.ndarray, sample_weights: np.ndarray) -> dict:
    (n_samples, n_features) = X.shape
    best_error = np.inf
    best_stump = {}
    for feature_idx in range(n_features):
        unique_thresholds = np.unique(X[:, feature_idx])
        for threshold in unique_thresholds:
            for polarity in [1, -1]:
                predictions = np.ones(n_samples)
                predictions[polarity * X[:, feature_idx] >= polarity * threshold] = -1
