def selu(x, order: int=0):
    alpha = 1.6732632423543772
    scale = 1.0507009873554805

    def elu(x):
        return np.where(x > 0, x, alpha * (np.exp(x) - 1))

    def elu_derivative(x):
        return np.where(x > 0, 1, alpha * np.exp(x))
    if order == 0:
        return (scale * elu(x)).round(4).tolist()
    elif order == 1:
        return (scale * elu_derivative(x)).round(4).tolist()
    elif order == 2:
        return (scale * alpha * np.exp(x)).round(4).tolist()
