def adaboost_1d_predict(x_train: list[float], y_train: list[int], x_test: list[float], epsilon: float=0.0) -> list[int]:
    n = len(x_train)
    m = len(x_test)
    sample_weights = np.ones(n) / n
    stumps = []
    alphas = []

    def calculate_error(y_true, y_pred, weights):
        return np.sum(weights * (y_true != y_pred))

    def calculate_weighted_error(y_true, y_pred, weights):
        return np.sum(weights * (y_true != y_pred))

    def update_sample_weights(weights, alpha, y_true, y_pred):
        return weights * np.exp(-alpha * y_true * y_pred)

    def normalize_weights(weights):
        return weights / np.sum(weights)
    while True:
        best_stump = None
        min_error = float('inf')
        for i in range(n):
            for j in range(i, n):
                threshold = (x_train[i] + x_train[j]) / 2
                for d in [0, 1]:
                    y_pred = np.ones(n)
                    if d == 0:
                        y_pred[x_train > threshold] = -1
                    else:
                        y_pred[x_train <= threshold] = -1
                    error = calculate_weighted_error(y_train, y_pred, sample_weights)
                    if error < min_error:
                        min_error = error
                        best_stump = (d, threshold)
        alpha = 0.5 * math.log((1 - min_error) / min_error)
        y_pred = np.ones(n)
        if best_stump[0] == 0:
            y_pred[x_train > best_stump[1]] = -1
        else:
            y_pred[x_train <= best_stump[1]] = -1
        sample_weights = update_sample_weights(sample_weights, alpha, y_train, y_pred)
        sample_weights = normalize_weights(sample_weights)
        stumps.append(best_stump)
        alphas.append(alpha)
        y_pred_train = np.zeros(n)
        for i in range(len(stumps)):
            (d, threshold) = stumps[i]
            if d == 0:
                y_pred_train[x_train > threshold] += alphas[i]
            else:
                y_pred_train[x_train <= threshold] += alphas[i]
        y_pred_train = np.sign(y_pred_train)
        training_error = np.mean(y_pred_train != y_train)
        if training_error <= epsilon:
            break
    y_pred_test = np.zeros(m)
    for i in range(len(stumps)):
        (d, threshold) = stumps[i]
        if d == 0:
            y_pred_test[x_test > threshold] += alphas[i]
        else:
            y_pred_test[x_test <= threshold] += alphas[i]
    y_pred_test = np.sign(y_pred_test)
    return y_pred_test.tolist()

