def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*."""
    ngram_counts = Counter()
    n_minus_1gram_counts = Counter()
    for i in range(len(corpus) - N + 1):
        ngram = tuple(corpus[i:i + N])
        n_minus_1gram = tuple(corpus[i:i + N - 1])
        ngram_counts[ngram] += 1
        n_minus_1gram_counts[n_minus_1gram] += 1
    log_prob = 0.0
    for i in range(len(sequence) - N + 1):
        ngram = tuple(sequence[i:i + N])
        n_minus_1gram = tuple(sequence[i:i + N - 1])
        ngram_count = ngram_counts[ngram]
        n_minus_1gram_count = n_minus_1gram_counts[n_minus_1gram]
        if n_minus_1gram_count == 0:
            return float('-inf')
        prob = ngram_count / n_minus_1gram_count
        if prob == 0:
            return float('-inf')
        log_prob += np.log(prob)
    return round(log_prob, 4)

import numpy as np
def lasso_regression(X: list[float] | 'np.ndarray', y: list[float] | 'np.ndarray', degree: int, reg_factor: float, n_iterations: int=1000, tol: float=1e-06) -> list[float]:
    X = np.array(X)
    y = np.array(y)
    X_design = np.vander(X, degree + 1, increasing=True)
    w = np.zeros(degree + 1)
    for _ in range(n_iterations):
        w_prev = np.copy(w)
        w[0] = np.mean(y - np.dot(X_design[:, 1:], w[1:]))
        for j in range(1, degree + 1):
            x_j = X_design[:, j]
            rho = x_j.dot(y - X_design.dot(w) + w[j] * x_j)
            w[j] = np.sign(rho) * max(abs(rho) - reg_factor, 0) / x_j.dot(x_j)
        if np.max(np.abs(w - w_prev)) < tol:
            break
    return np.round(w, 4).tolist()

