TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    encoded_text = [vocabulary.get(char, 0) for char in text]
    if len(encoded_text) < time_steps:
        encoded_text += [0] * (time_steps - len(encoded_text))
    else:
        encoded_text = encoded_text[:time_steps]
    return encoded_text
