from scipy.spatial.distance import cdist
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    if metric == 'manhattan':
        dist_matrix = cdist(X_test, X_train, metric='cityblock')
    elif metric == 'cosine':
        dist_matrix = 1 - np.dot(X_test, X_train.T) / (np.linalg.norm(X_test) * np.linalg.norm(X_train, axis=1))
    else:
        dist_matrix = cdist(X_test, X_train, metric='euclidean')
    closest_indices = np.argsort(dist_matrix, axis=1)[:, :k]
    closest_labels = y_train[closest_indices]
    predicted_labels = np.array([np.argmax(np.bincount(labels)) for labels in closest_labels])
    return predicted_labels

import numpy as np
def gini_impurity(y):
    """Calculate the Gini impurity for a given array of class labels."""
    if len(y) == 0:
        return 0
    (class_labels, counts) = np.unique(y, return_counts=True)
    probabilities = counts / len(y)
    return 1 - np.sum(probabilities ** 2)
def best_split(X, y):
    """Find the best feature and threshold to split the data."""
    best_gain = 0
    best_feature = None
    best_threshold = None
    parent_impurity = gini_impurity(y)
    (n_samples, n_features) = X.shape
    for feature_index in range(n_features):
        (thresholds, classes) = zip(*sorted(zip(X[:, feature_index], y)))
        for i in range(1, n_samples):
            if classes[i] != classes[i - 1]:
                threshold = (thresholds[i] + thresholds[i - 1]) / 2
                left_indices = thresholds < threshold
                right_indices = thresholds >= threshold
                if np.any(left_indices) and np.any(right_indices):
                    left_impurity = gini_impurity(classes[:i])
                    right_impurity = gini_impurity(classes[i:])
                    weighted_impurity = (i * left_impurity + (n_samples - i) * right_impurity) / n_samples
                    gain = parent_impurity - weighted_impurity
                    if gain > best_gain:
                        best_gain = gain
                        best_feature = feature_index
                        best_threshold = threshold
    return (best_feature, best_threshold)
class Node:
    """A node in the decision tree."""

    def __init__(self, feature=None, threshold=None, left=None, right=None, value=None):
        self.feature = feature
        self.threshold = threshold
        self.left = left
        self.right = right
        self.value = value
def build_tree(X, y, depth=0, max_depth=None):
    """Recursively build the decision tree."""
    if len(set(y)) == 1:
        return Node(value=y[0])
    if max_depth is not None and depth >= max_depth:
        majority_class = np.bincount(y).argmax()
        return Node(value=majority_class)
    (feature, threshold) = best_split(X, y)
    if feature is None:
        majority_class = np.bincount(y).argmax()
        return Node(value=majority_class)
    left_indices = X[:, feature] < threshold
    right_indices = X[:, feature] >= threshold
    left_node = build_tree(X[left_indices], y[left_indices], depth + 1, max_depth)
    right_node = build_tree(X[right_indices], y[right_indices], depth + 1, max_depth)
    return Node(feature, threshold, left_node, right_node)
def predict_sample(node, sample):
    """Predict the class label for a single sample."""
    if node.value is not None:
        return node.value
    if sample[node.feature] < node.threshold:
        return predict_sample(node.left, sample)
    else:
        return predict_sample(node.right, sample)
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""
    tree = build_tree(X_train, y_train, max_depth=max_depth)
    predictions = [predict_sample(tree, sample) for sample in X_test]
    return predictions

import numpy as np
def best_gini_split(X, y):

    def gini_impurity(labels):
        (_, counts) = np.unique(labels, return_counts=True)
        probabilities = counts / len(labels)
        return 1 - np.sum(probabilities ** 2)

    def weighted_gini(left_labels, right_labels):
        n_left = len(left_labels)
        n_right = len(right_labels)
        n_total = n_left + n_right
        gini_left = gini_impurity(left_labels)
        gini_right = gini_impurity(right_labels)
        return n_left / n_total * gini_left + n_right / n_total * gini_right
    best_gini = 1.0
    best_feature_index = -1
    best_threshold_value = None
    (n_samples, n_features) = X.shape
    G_whole_dataset = gini_impurity(y)
    for feature_index in range(n_features):
        unique_values = np.unique(X[:, feature_index])
        for threshold in unique_values:
            left_indices = np.where(X[:, feature_index] <= threshold)[0]
            right_indices = np.where(X[:, feature_index] > threshold)[0]
            if len(left_indices) == 0 or len(right_indices) == 0:
                continue
            left_labels = y[left_indices]
            right_labels = y[right_indices]
            current_gini = weighted_gini(left_labels, right_labels)
            if current_gini < best_gini:
                best_gini = current_gini
                best_feature_index = feature_index
                best_threshold_value = threshold
            elif np.isclose(current_gini, best_gini, atol=1e-12):
                if feature_index < best_feature_index or (feature_index == best_feature_index and threshold < best_threshold_value):
                    best_feature_index = feature_index
                    best_threshold_value = threshold
    if best_feature_index == -1:
        return (-1, None, round(G_whole_dataset, 4))
    else:
        return (best_feature_index, best_threshold_value, round(best_gini, 4))

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_vectors(x, y):
        """Validate the input vectors."""
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return False
        if len(x) != len(y) or len(x) == 0:
            return False
        try:
            x = np.asarray(x, dtype=float)
            y = np.asarray(y, dtype=float)
        except ValueError:
            return False
        return x.ndim == 1 and y.ndim == 1

    def euclidean(x, y):
        return round(np.sqrt(np.sum((x - y) ** 2)), 4)

    def manhattan(x, y):
        return round(np.sum(np.abs(x - y)), 4)

    def chebyshev(x, y):
        return round(np.max(np.abs(x - y)), 4)

    def cosine(x, y):
        norm_x = np.linalg.norm(x)
