def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:

    def hz_to_mel(f):
        return 2595 * np.log10(1 + f / 700)

    def mel_to_hz(m):
        return 700 * (10 ** (m / 2595) - 1)
    if max_freq is None:
        max_freq = fs / 2
    min_mel = hz_to_mel(min_freq)
    max_mel = hz_to_mel(max_freq)
    mel_points = np.linspace(min_mel, max_mel, n_filters + 2)
    freq_points = [mel_to_hz(m) for m in mel_points]
    filterbank = np.zeros((n_filters, N // 2 + 1))
    for i in range(n_filters):
        left_slope = (freq_points[i] - freq_points[i - 1]) / (freq_points[i] - freq_points[i - 1])
        right_slope = (freq_points[i + 2] - freq_points[i + 1]) / (freq_points[i + 2] - freq_points[i + 1])
        for k in range(N // 2 + 1):
            if k * fs / N < freq_points[i - 1] or k * fs / N > freq_points[i + 2]:
                continue
            if k * fs / N <= freq_points[i]:
                filterbank[i, k] = left_slope * (k * fs / N - freq_points[i - 1])
            elif k * fs / N >= freq_points[i + 1]:
                filterbank[i, k] = right_slope * (freq_points[i + 2] - k * fs / N)
            else:
                filterbank[i, k] = 1
    if normalize:
        for i in range(n_filters):
            filterbank[i] *= 2 / (freq_points[i + 2] - freq_points[i])
    return [[round(val, 4) for val in row] for row in filterbank.tolist()]

