def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """

    def sigmoid_func(val):
        return 1 / (1 + np.exp(-val))

    def sigmoid_derivative(val):
        sig = sigmoid_func(val)
        return sig * (1 - sig)

    def sigmoid_second_derivative(val):
        sig = sigmoid_func(val)
        return sig * (1 - sig) * (1 - 2 * sig)
    if order == 0:
        return np.round(sigmoid_func(x), 4).tolist() if isinstance(x, (list, np.ndarray)) else round(sigmoid_func(x), 4)
    elif order == 1:
        return np.round(sigmoid_derivative(x), 4).tolist() if isinstance(x, (list, np.ndarray)) else round(sigmoid_derivative(x), 4)
    elif order == 2:
        return np.round(sigmoid_second_derivative(x), 4).tolist() if isinstance(x, (list, np.ndarray)) else round(sigmoid_second_derivative(x), 4)
    else:
        return -1

