def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:

    def region_query(data, point_index, eps):
        neighbors = []
        for i in range(len(data)):
            if np.linalg.norm(data[point_index] - data[i]) <= eps:
                neighbors.append(i)
        return neighbors

    def expand_cluster(data, point_index, neighbors, cluster_id, eps, min_samples, clusters, visited):
        clusters[point_index] = cluster_id
        i = 0
        while i < len(neighbors):
            neighbor_index = neighbors[i]
            if visited[neighbor_index] == False:
                visited[neighbor_index] = True
                new_neighbors = region_query(data, neighbor_index, eps)
                if len(new_neighbors) >= min_samples:
                    neighbors += new_neighbors
            if clusters[neighbor_index] == -1:
                clusters[neighbor_index] = cluster_id
            i += 1
    if len(data) == 0:
        return []
    clusters = [-1] * len(data)
    visited = [False] * len(data)
    cluster_id = 0
    for i in range(len(data)):
        if visited[i]:
            continue
        visited[i] = True
        neighbors = region_query(data, i, eps)
        if len(neighbors) < min_samples:
            clusters[i] = -1
        else:
            expand_cluster(data, i, neighbors, cluster_id, eps, min_samples, clusters, visited)
            cluster_id += 1
    return clusters
