def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """

    def split_node(X, y, depth):
        if len(X) < 2 or depth == max_depth:
            return np.mean(y)
        best_sse = float('inf')
        best_split = None
        for i in range(1, len(X)):
            (left_X, right_X) = (X[:i], X[i:])
            (left_y, right_y) = (y[:i], y[i:])
            sse = np.sum((left_y - np.mean(left_y)) ** 2) + np.sum((right_y - np.mean(right_y)) ** 2)
            if sse < best_sse:
                best_sse = sse
                best_split = (left_X, right_X, left_y, right_y)
        if best_split is None:
            return np.mean(y)
        (left_X, right_X, left_y, right_y) = best_split
        return {'split_value': X[len(left_X)], 'left': split_node(left_X, left_y, depth + 1), 'right': split_node(right_X, right_y, depth + 1)}
    tree = split_node(X_train, y_train, 0)

    def predict_tree(tree, x):
        if isinstance(tree, (float, np.float64)):
            return tree
        elif x < tree['split_value']:
            return predict_tree(tree['left'], x)
        else:
            return predict_tree(tree['right'], x)
    return [predict_tree(tree, x) for x in X_test]

