        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """

    def build_header_table(transactions):
        header_table = defaultdict(list)
        for transaction in transactions:
            for item in transaction:
                header_table[item].append(None)
        return header_table

    def build_fp_tree(transactions, min_sup):
        header_table = build_header_table(transactions)
        fp_tree = FPNode(None, None, None)
        for transaction in transactions:
            ordered_items = [item for item in transaction if item in header_table]
            if len(ordered_items) > 0:
                insert_ordered_items(fp_tree, ordered_items, header_table, min_sup)
        return (fp_tree, header_table)

    def insert_ordered_items(node, ordered_items, header_table, min_sup):
        if ordered_items[0] not in node.children:
            new_node = FPNode(ordered_items[0], node, header_table[ordered_items[0]][0])
            node.children[ordered_items[0]] = new_node
            header_table[ordered_items[0]][0] = new_node
        else:
            new_node = node.children[ordered_items[0]]
            header_table[ordered_items[0]][-1].link = new_node
            header_table[ordered_items[0]].append(new_node)
        if len(ordered_items) > 1:
            insert_ordered_items(new_node, ordered_items[1:], header_table, min_sup)
        else:
            new_node.increment_count()

    def mine_fp_tree(fp_tree, header_table, min_sup, prefix, frequent_item_sets):
        for (item, nodes) in sorted(header_table.items(), key=lambda x: x[0]):
            support = sum((node.count for node in nodes))
            if support >= min_sup and item not in prefix:
                new_prefix = prefix + [item]
                frequent_item_sets.append(tuple(sorted(new_prefix)))
                conditional_pattern_base = []
                for node in nodes:
                    frequency = node.count
                    path = []
                    while node.parent.item is not None:
                        path.append(node.parent.item)
                        node = node.parent
                    for _ in range(frequency):
                        conditional_pattern_base.append(path)
                (conditional_fp_tree, conditional_header_table) = build_fp_tree(conditional_pattern_base, min_sup)
                if conditional_fp_tree.children:
                    mine_fp_tree(conditional_fp_tree, conditional_header_table, min_sup, new_prefix, frequent_item_sets)

    class FPNode:

        def __init__(self, item, parent, node_link):
            self.item = item
            self.parent = parent
            self.node_link = node_link
            self.children = {}
            self.count = 1

        def increment_count(self):
            self.count += 1
    frequent_item_sets = []
    (fp_tree, header_table) = build_fp_tree(transactions, min_sup)
    mine_fp_tree(fp_tree, header_table, min_sup, [], frequent_item_sets)
    return sorted(frequent_item_sets, key=lambda x: (len(x), x))
