def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    """
    Implements a deterministic Pegasos kernel SVM training algorithm.

    Parameters:
        data (np.ndarray): 2D array of shape (n_samples, n_features)
        labels (np.ndarray): 1D array of shape (n_samples,)
        kernel (str): 'linear' or 'rbf'
        lambda_val (float): regularization parameter
        iterations (int): number of iterations
        sigma (float): parameter for RBF kernel

    Returns:
        alpha_list (list): list of alpha coefficients (as list), shape (n_samples,)
        bias (float): bias term
    """
    n_samples = data.shape[0]
    alpha = np.zeros(n_samples)
    bias = 0.0

    def kernel_func(x1, x2):
        if kernel == 'linear':
            return np.dot(x1, x2)
        elif kernel == 'rbf':
            diff = x1 - x2
            return np.exp(-np.dot(diff, diff) / (2 * sigma ** 2))
        else:
            raise ValueError("Unsupported kernel. Choose 'linear' or 'rbf'.")
    K = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            K[i, j] = kernel_func(data[i], data[j])
    for t in range(1, iterations + 1):
        decision = np.dot(alpha * labels, K) + bias
        margins = labels * decision
        eta = 1.0 / (lambda_val * t)
        for i in range(n_samples):
            if margins[i] < 1:
                alpha[i] = (1 - eta * lambda_val) * alpha[i] + eta
                bias = (1 - eta * lambda_val) * bias + eta * labels[i]
            else:
                alpha[i] = (1 - eta * lambda_val) * alpha[i]
                bias = (1 - eta * lambda_val) * bias
    alpha_rounded = np.round(alpha, 4)
    bias_rounded = np.round(bias, 4)
    alpha_list = alpha_rounded.tolist()
    return (alpha_list, float(bias_rounded))

