def rref(matrix):
    """
    Convert the given matrix to its Reduced Row Echelon Form (RREF).

    Args:
        matrix (list of list of floats): The input matrix.

    Returns:
        list of list of floats: The RREF of the input matrix.
    """
    A = np.array(matrix, dtype=float)
    (rows, cols) = A.shape
    lead = 0
    for r in range(rows):
        if lead >= cols:
            break
        i = r
        while i < rows and np.isclose(A[i, lead], 0):
            i += 1
        if i == rows:
            lead += 1
            continue
        if i != r:
            A[[r, i]] = A[[i, r]]
        pivot = A[r, lead]
        if not np.isclose(pivot, 0):
            A[r] = A[r] / pivot
        for i in range(rows):
            if i != r:
                factor = A[i, lead]
                A[i] = A[i] - factor * A[r]
        lead += 1
    A[np.isclose(A, 1e-12)] = 0
    return A.tolist()

