def gauss_seidel(A, b, n, x_ini=None):
    A = np.array(A, dtype=float)
    b = np.array(b, dtype=float)
    size = A.shape[0]
    if x_ini is None:
        x = np.zeros(size)
    else:
        x = np.array(x_ini, dtype=float)
    for _ in range(n):
        for i in range(size):
            sum_ = 0
            for j in range(size):
                if j != i:
                    sum_ += A[i][j] * x[j]
            x[i] = (b[i] - sum_) / A[i][i]
    x_rounded = np.round(x, 4)
    return x_rounded.tolist()

