def gaussian_elimination(A, b):
    A = np.array(A, dtype=float)
    b = np.array(b, dtype=float)
    n = len(b)
    for i in range(n):
        max_row = i + np.argmax(np.abs(A[i:, i]))
        if A[max_row, i] == 0:
            raise ValueError('Matrix is singular or nearly singular.')
        if max_row != i:
            A[[i, max_row]] = A[[max_row, i]]
            b[[i, max_row]] = b[[max_row, i]]
        for j in range(i + 1, n):
            factor = A[j, i] / A[i, i]
            A[j, i:] = A[j, i:] - factor * A[i, i:]
            b[j] = b[j] - factor * b[i]
    x = np.zeros(n)
    for i in reversed(range(n)):
        if A[i, i] == 0:
            raise ValueError('Matrix is singular or nearly singular.')
        x[i] = (b[i] - np.dot(A[i, i + 1:], x[i + 1:])) / A[i, i]
    x = np.round(x, 4)
    return x.tolist()

import numpy as np
def compute_tf_idf(corpus, query):
    if not corpus:
        return []
    N = len(corpus)
    vocab = set()
    for doc in corpus:
        vocab.update(doc)
    vocab = list(vocab)
    term_index = {term: idx for (idx, term) in enumerate(vocab)}
    dt_matrix = np.zeros((N, len(vocab)), dtype=float)
    for (doc_idx, doc) in enumerate(corpus):
        for word in doc:
            if word in term_index:
                dt_matrix[doc_idx, term_index[word]] += 1
    df = np.count_nonzero(dt_matrix > 0, axis=0)
    idf = np.log((N + 1) / (df + 1)) + 1
    tf_matrix = np.zeros_like(dt_matrix)
    for (i, doc) in enumerate(corpus):
        total_terms = len(doc)
        if total_terms > 0:
            for word in doc:
                if word in term_index:
                    tf_matrix[i, term_index[word]] += 1
            tf_matrix[i] /= total_terms
    results = []
    for doc_idx in range(N):
        tfidf_scores = []
        for q_word in query:
            if q_word in term_index:
                tf = tf_matrix[doc_idx, term_index[q_word]]
                score = tf * idf[term_index[q_word]]
            else:
                score = 0.0
            tfidf_scores.append(round(score, 4))
        results.append(tfidf_scores)
    return results

def compressed_col_sparse_matrix(dense_matrix):
    """
    Convert a dense matrix into its Compressed Column Sparse (CSC) representation.

    :param dense_matrix: List of lists representing the dense matrix
    :return: Tuple of (values, row indices, column pointer)
    """
    if not dense_matrix:
        return ([], [], [])
    num_rows = len(dense_matrix)
    num_cols = len(dense_matrix[0])
    values = []
    row_indices = []
    col_pointer = [0]
    for col in range(num_cols):
        for row in range(num_rows):
            val = dense_matrix[row][col]
            if val != 0:
                values.append(val)
                row_indices.append(row)
        col_pointer.append(len(values))
    return (values, row_indices, col_pointer)

