def pos_encoding(position: int, d_model: int):
    if position == 0 or d_model <= 0:
        return -1
    angle_rads = np.zeros((position, d_model), dtype=np.float16)
    positions = np.arange(position).reshape(-1, 1)
    div_term = np.exp(np.arange(0, d_model, 2) * -(np.log(10000.0) / d_model)).astype(np.float16)
    angle_rads[:, 0::2] = np.sin(positions * div_term)
