def min_max(x: list[int]) -> list[float]:
    if not x:
        return []
    min_val = min(x)
    max_val = max(x)
    if min_val == max_val:
        return [round(0.0, 4) for _ in x]
    normalized = []
    for value in x:
        norm_value = (value - min_val) / (max_val - min_val)
        normalized.append(round(norm_value, 4))
    return normalized

def poly_term_derivative(c: float, x: float, n: float) -> float:
    derivative_value = c * n * x ** (n - 1)
    return round(derivative_value, 4)

import numpy as np
def bhattacharyya_distance(p: list[float], q: list[float]) -> float:
    if not p or not q or len(p) != len(q):
        return 0.0
    p_array = np.array(p)
    q_array = np.array(q)
    coefficient = np.sum(np.sqrt(p_array * q_array))
    coefficient = min(max(coefficient, 0), 1)
    distance = -np.log(coefficient)
    return round(distance, 4)

def vector_sum(a: list[int | float], b: list[int | float]) -> list[int | float]:
    if len(a) != len(b):
        return -1
    return [x + y for (x, y) in zip(a, b)]

import numpy as np
def find_treasure(start_x: float, learning_rate: float=0.1, tolerance: float=1e-06, max_iters: int=10000) -> float:

    def f(x):
        return x ** 4 - 3 * x ** 3 + 2

    def df(x):
        return 4 * x ** 3 - 9 * x ** 2
    x = start_x
    for _ in range(max_iters):
        gradient = df(x)
        new_x = x - learning_rate * gradient
        if abs(new_x - x) < tolerance:
            break
        x = new_x
    return round(x, 4)
