import numpy as np
import numpy as np
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Predict the class labels for the test set using k-NN classifier."""

    def euclidean_distance(a, b):
        return np.sqrt(np.sum((a - b) ** 2))

    def manhattan_distance(a, b):
        return np.sum(np.abs(a - b))

    def cosine_distance(a, b):
        return 1 - np.dot(a, b) / (np.linalg.norm(a) * np.linalg.norm(b))
    if metric == 'euclidean':
        distance_function = euclidean_distance
    elif metric == 'manhattan':
        distance_function = manhattan_distance
    elif metric == 'cosine':
        distance_function = cosine_distance
    else:
        distance_function = euclidean_distance
    predictions = []
    for test_sample in X_test:
        distances = np.array([distance_function(test_sample, train_sample) for train_sample in X_train])
        k_indices = np.argsort(distances)[:k]
        k_nearest_labels = y_train[k_indices]
        (unique_labels, counts) = np.unique(k_nearest_labels, return_counts=True)
        max_count_index = np.argmax(counts)
        if np.sum(counts == counts[max_count_index]) > 1:
            predicted_label = unique_labels[np.argmin(unique_labels[counts == counts[max_count_index]])]
        else:
            predicted_label = unique_labels[max_count_index]
        predictions.append(predicted_label)
    return np.array(predictions)
