def build_fp_tree(transactions, min_support):
    item_count = Counter((item for transaction in transactions for item in transaction))
    item_count = {item: count for (item, count) in item_count.items() if count >= min_support}
    if not item_count:
        return (None, None)
    sorted_items = [item for (item, _) in sorted(item_count.items(), key=lambda x: (-x[1], x[0]))]
    root = TreeNode('root', 0)
    for transaction in transactions:
        filtered_items = [item for item in sorted_items if item in transaction]
        if filtered_items:
            current_node = root
            for item in filtered_items:
                if item in current_node.children:
                    current_node.children[item].count += 1
                else:
                    new_node = TreeNode(item, 1, current_node)
                    current_node.children[item] = new_node
                current_node = current_node.children[item]
    header_table = {}
    for item in sorted_items:
        header_table[item] = None
    for (item, node) in header_table.items():
        if node is None:
            current_node = root
            while current_node:
                if item in current_node.children:
                    if header_table[item] is None:
                        header_table[item] = current_node.children[item]
                    else:
                        current_node.children[item].node_link = header_table[item]
                        header_table[item] = current_node.children[item]
