TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    """Encodes a string into a fixed-length list of integer token ids."""
    encoded = []
    for char in text:
        encoded.append(vocabulary.get(char, 0))
    if len(encoded) < time_steps:
        encoded += [0] * (time_steps - len(encoded))
    else:
        encoded = encoded[:time_steps]
    return encoded
def int_to_string(indices: list[int], inverse_vocab: dict[int, str]) -> str:
    """Converts a list of integer ids back to a human-readable string."""
    decoded = []
    for index in indices:
        if index != 0:
            decoded.append(inverse_vocab.get(index, ''))
    return ''.join(decoded)
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    """Encodes the input text, predicts using the model, and decodes the output."""
    encoded_input = string_to_int(text, TIME_STEPS, input_vocabulary)
    input_array = np.array(encoded_input).reshape(1, -1)
    predictions = model.predict(input_array)
    predicted_ids = np.argmax(predictions, axis=-1)[0]
    decoded_output = int_to_string(predicted_ids, inv_output_vocabulary)
    return decoded_output

from collections import deque
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    fire_time = [[float('inf')] * n for _ in range(n)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_queue.append((i, j))
                fire_time[i][j] = 0
    while fire_queue:
        (x, y) = fire_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] == '.') and (fire_time[nx][ny] == float('inf')):
                fire_time[nx][ny] = fire_time[x][y] + 1
                fire_queue.append((nx, ny))
    agent_queue = deque([(0, 0, 0)])
    visited = [[False] * n for _ in range(n)]
    visited[0][0] = True
    while agent_queue:
        (x, y, time) = agent_queue.popleft()
        if x == n - 1 and y == n - 1:
            return time
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (not visited[nx][ny]):
                if grid[nx][ny] == '.' and time + 1 < fire_time[nx][ny]:
                    visited[nx][ny] = True
                    agent_queue.append((nx, ny, time + 1))
    return -1

