def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Predict the class labels for the test set using k-NN classifier."""

    def euclidean_distance(a, b):
        return np.sqrt(np.sum((a - b) ** 2))

    def manhattan_distance(a, b):
        return np.sum(np.abs(a - b))

    def cosine_distance(a, b):
        return 1 - np.dot(a, b) / (np.linalg.norm(a) * np.linalg.norm(b))
    if metric == 'euclidean':
        distance_function = euclidean_distance
    elif metric == 'manhattan':
        distance_function = manhattan_distance
    elif metric == 'cosine':
        distance_function = cosine_distance
    else:
        distance_function = euclidean_distance
    predictions = []
    for test_sample in X_test:
        distances = np.array([distance_function(test_sample, train_sample) for train_sample in X_train])
        k_indices = np.argsort(distances)[:k]
        k_nearest_labels = y_train[k_indices]
        (unique_labels, counts) = np.unique(k_nearest_labels, return_counts=True)
        max_count_index = np.argmax(counts)
        if np.sum(counts == counts[max_count_index]) > 1:
            predicted_label = unique_labels[np.argmin(unique_labels[counts == counts[max_count_index]])]
        else:
            predicted_label = unique_labels[max_count_index]
        predictions.append(predicted_label)
    return np.array(predictions)

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_vectors(x, y):
        """Validate that x and y are one-dimensional, non-empty, equal-length numeric iterables."""
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return False
        if len(x) != len(y) or len(x) == 0:
            return False
        try:
            x = np.asarray(x, dtype=float)
            y = np.asarray(y, dtype=float)
        except ValueError:
            return False
        if x.ndim != 1 or y.ndim != 1:
            return False
        return (x, y)

    def euclidean(x, y):
        return np.sqrt(np.sum((x - y) ** 2))

    def manhattan(x, y):
        return np.sum(np.abs(x - y))

    def chebyshev(x, y):
        return np.max(np.abs(x - y))

    def cosine(x, y):
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        return 1 - np.dot(x, y) / (norm_x * norm_y)

    def distance_function(x, y):
        validated = validate_vectors(x, y)
        if validated is False:
            return -1
        (x, y) = validated
        if name == 'euclidean':
            return round(euclidean(x, y), 4)
        elif name == 'manhattan':
            return round(manhattan(x, y), 4)
        elif name == 'chebyshev':
            return round(chebyshev(x, y), 4)
        elif name == 'cosine':
            return round(cosine(x, y), 4)
        else:
            return -1
    return distance_function

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """

    def apply_leaky_relu(x):
        return x if x >= 0 else a * x
    if isinstance(z, np.ndarray):
        return z.tolist() if a == 0.01 else np.where(z >= 0, z, a * z).tolist()
    elif isinstance(z, (int, float)):
        return apply_leaky_relu(z)
    elif isinstance(z, list):
        return [leaky_relu(item, a) for item in z]
    else:
        raise TypeError('Input must be a scalar, list, or numpy.ndarray.')

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool) -> PyTuple[bool, bool]:
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def is_space_continuous(space: Space) -> bool:
        if isinstance(space, Box):
            return True
        elif isinstance(space, Tuple):
            return all((is_space_continuous(sub_space) for sub_space in space.spaces))
        elif isinstance(space, Dict):
            return all((is_space_continuous(sub_space) for sub_space in space.spaces.values()))
        else:
            return False
    if tuple_action:
        cont_action = is_space_continuous(env.action_space)
    else:
        cont_action = isinstance(env.action_space, Box)
    if tuple_obs:
        cont_obs = is_space_continuous(env.observation_space)
    else:
        cont_obs = isinstance(env.observation_space, Box)
    return (cont_action, cont_obs)

import numpy as np
class TreeNode:

    def __init__(self, value=None, feature_index=None, threshold=None, left=None, right=None):
        self.value = value
        self.feature_index = feature_index
        self.threshold = threshold
        self.left = left
