            if not found_child:
                current_node = current_node.child[1]
        elif task_type == 'regression':
            if feature_value <= current_node.s:
                current_node = current_node.child[0]
            else:
                current_node = current_node.child[1]

import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    feature_counts = defaultdict(lambda : defaultdict(int))
    label_counts = defaultdict(int)
    for (x, y) in zip(X_train, y_train):
        label_counts[y] += 1
        for (feature_index, feature_value) in enumerate(x):
            feature_counts[feature_index, feature_value][y] += 1
    total_samples = len(y_train)
    weights = defaultdict(float)
    for ((feature_index, feature_value), labels) in feature_counts.items():
        for label in labels:
            weights[feature_index, feature_value, label] = 1.0
    for iteration in range(n_iter):
        expected_counts = defaultdict(float)
        for (x, y) in zip(X_train, y_train):
            unnormalized_probs = defaultdict(float)
            for (feature_index, feature_value) in enumerate(x):
                for label in label_counts.keys():
                    unnormalized_probs[label] += weights[feature_index, feature_value, label]
            Z_x = sum((math.exp(unnormalized_prob) for unnormalized_prob in unnormalized_probs.values()))
            probabilities = {label: math.exp(unnormalized_probs[label]) / Z_x for label in label_counts.keys()}
            for (feature_index, feature_value) in enumerate(x):
                for label in label_counts.keys():
                    expected_counts[feature_index, feature_value, label] += probabilities[label]
        max_update = 0.0
        for ((feature_index, feature_value), labels) in feature_counts.items():
            for label in labels:
                empirical_count = feature_counts[feature_index, feature_value][label] / total_samples
                expected_count = expected_counts[feature_index, feature_value, label]
                update = empirical_count - expected_count
                weights[feature_index, feature_value, label] += update
                max_update = max(max_update, abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for x in X_test:
        unnormalized_probs = defaultdict(float)
        for (feature_index, feature_value) in enumerate(x):
            for label in label_counts.keys():
                unnormalized_probs[label] += weights[feature_index, feature_value, label]
        Z_x = sum((math.exp(unnormalized_prob) for unnormalized_prob in unnormalized_probs.values()))
        probabilities = {label: math.exp(unnormalized_probs[label]) / Z_x for label in label_counts.keys()}
        predicted_label = max(probabilities, key=probabilities.get)
        predictions.append(predicted_label)
    return predictions

import numpy as np
def local_outlier_factor(data, k, epsilon=1.5):
    """Compute the Local Outlier Factor (LOF) for a given dataset."""
    n = data.shape[0]
    dist_matrix = np.linalg.norm(data[:, np.newaxis] - data, axis=2)
    k_distances = np.zeros(n)
    k_nearest_neighbors = np.zeros((n, k), dtype=int)
    for i in range(n):
        neighbors_indices = np.argsort(dist_matrix[i])[:k + 1][1:]
        k_nearest_neighbors[i] = neighbors_indices
        k_distances[i] = dist_matrix[i, neighbors_indices[-1]]
    reachability_distances = np.zeros((n, n))
    for i in range(n):
        for j in range(n):
            if j in k_nearest_neighbors[i]:
                reachability_distances[i, j] = max(k_distances[j], dist_matrix[i, j])
            else:
                reachability_distances[i, j] = np.inf
    LRD = np.zeros(n)
    for i in range(n):
        sum_reach_dist = np.sum(reachability_distances[i, k_nearest_neighbors[i]])
        LRD[i] = k / sum_reach_dist if sum_reach_dist > 0 else 0
    LOF = np.zeros(n)
    for i in range(n):
        sum_LRD = np.sum(LRD[k_nearest_neighbors[i]])
        LOF[i] = sum_LRD / k / LRD[i] if LRD[i] > 0 else 0
    LOF_rounded = np.round(LOF, 4)
    outlier_indices = np.where(LOF_rounded > epsilon)[0].tolist()
    return (LOF_rounded.tolist(), outlier_indices)

import numpy as np
def kmeans_centroids(X: 'np.ndarray', k: int, max_iters: int=100) -> list[list[float]]:
    """Compute centroids using K-Means clustering algorithm."""
    centroids = X[:k].copy()
    for _ in range(max_iters):
        distances = np.linalg.norm(X[:, np.newaxis] - centroids, axis=2) ** 2
        labels = np.argmin(distances, axis=1)
        new_centroids = np.zeros_like(centroids)
        for i in range(k):
            assigned_samples = X[labels == i]
            if len(assigned_samples) > 0:
                new_centroids[i] = assigned_samples.mean(axis=0)
            else:
                new_centroids[i] = centroids[i]
        if np.linalg.norm(new_centroids - centroids) < 0.0001:
            break
        centroids = new_centroids
    sorted_centroids = sorted(map(lambda c: [round(coord, 4) for coord in c], centroids))
    return sorted_centroids
