    else:
        return np.round(np.log1p(np.exp(x) - 1), 4)

import numpy as np
TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    """Converts a raw text string into a fixed-length list of integer token ids."""
    encoded_ids = []
    for char in text:
        if char in vocabulary:
            encoded_ids.append(vocabulary[char])
        else:
            encoded_ids.append(0)
    if len(encoded_ids) < time_steps:
        encoded_ids += [0] * (time_steps - len(encoded_ids))
    return encoded_ids
def int_to_string(indices, inverse_vocab: dict[int, str]) -> str:
    """Converts a list of integer ids back to text using an inverse vocabulary."""
    decoded_text = ''
    for index in indices:
        if index in inverse_vocab:
            decoded_text += inverse_vocab[index]
        else:
            decoded_text += '0'
    return decoded_text
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    """Runs the character-level sequence encoding and decoding process."""
    encoded_ids = string_to_int(text, TIME_STEPS, input_vocabulary)
    output = model.predict(encoded_ids)
    predicted_ids = np.argmax(output, axis=-1)
    decoded_text = int_to_string(predicted_ids, inv_output_vocabulary)
    return decoded_text

import numpy as np
def metric(name: str) -> callable:
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def d(x: list, y: list) -> float:
        if not isinstance(x, (list, tuple, np.ndarray)):
            return -1
        if len(x) != len(y):
            return -1
        if len(x) == 0 or len(y) == 0:
            return -1
        if name not in ['euclidean', 'manhattan', 'chebyshev', 'cosine']:
            return -1
        if name == 'cosine':
            if np.allclose(np.linalg.norm(x), 0) or np.allclose(np.linalg.norm(y), 0):
                return -1
        if name == 'euclidean':
            return np.sqrt(np.sum((x - y) ** 2))
        elif name == 'manhattan':
            return np.sum(np.abs(x - y))
        elif name == 'chebyshev':
            return np.max(np.abs(x - y))
        else:
            return 1 - x * y / (np.linalg.norm(x) * np.linalg.norm(y))
    return d

