def action_stats(env: Any, md_action: bool, cont_action: bool) -> Tuple[List[Union[int, float]], Union[List[Tuple[int, ...]], None], int]:
    """Summarise an RL environment's action space.

    Args:
        env: Environment instance exposing an ``action_space`` attribute mimicking OpenAI Gym.
        md_action: ``True`` if the action space is multi-dimensional.
        cont_action: ``True`` if the action space is continuous (infinite number of actions).

    Returns:
        A tuple ``(n_actions_per_dim, action_ids, action_dim)`` where
            • n_actions_per_dim: list with the number of actions in every dimension
                                  (``numpy.inf`` for continuous ones),
            • action_ids: list of all discrete actions (cartesian product) or ``None`` if
                           any dimension is continuous,
            • action_dim: number of action dimensions.
    """
    if md_action and cont_action:
        return (None, None, 0)
    elif md_action:
        return (None, None, 0)
    elif cont_action:
        return (None, None, 0)
    else:
        action_dim = env.action_space.n
        action_ids = env.action_space.spaces[0].n
        return ([np.inf] * action_dim, action_ids, action_dim)

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """
    Performs forward and backward passes of a 2-D average-pooling layer.

    Args:
    X (np.ndarray): Input feature map with shape (N, C, H, W)
    pool_shape (tuple[int, int]): Pooling window size
    stride (tuple[int, int]): Stride of the pooling operation
    accum_grad (np.ndarray): Gradient of the loss with respect to the pooled output

    Returns:
    tuple[list, list]: Pooled output and gradient of the loss with respect to the pooled output
    """
    pooled_output = np.zeros_like(X, dtype=X.dtype)
    for i in range(X.shape[0]):
        for j in range(X.shape[1]):
            for k in range(X.shape[2]):
                for l in range(X.shape[3]):
                    window_sum = np.sum(X[i, j, k, l] * np.exp(-((i - k) / stride[0]) ** 2 - ((j - l) / stride[1]) ** 2))
                    pooled_output[i, j, k, l] += window_sum * np.exp(-((i - k) / stride[0]) ** 2 - ((j - l) / stride[1]) ** 2)
    grad_input = np.zeros_like(X, dtype=X.dtype)
    for i in range(X.shape[0]):
        for j in range(X.shape[1]):
            for k in range(X.shape[2]):
                for l in range(X.shape[3]):
                    grad_input[i, j, k, l] = accum_grad[i, j, k, l] - pooled_output[i, j, k, l]
    pooled_output = [list((map(round, x) for x in pooled_output.tolist())) for i in range(X.shape[0])]
    grad_input = [list((map(round, x) for x in grad_input.tolist()))]
    return (pooled_output, grad_input)

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
def compare_trees(tree_a, tree_b):
    """
    Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        return np.allclose(tree_a.value, tree_b.value)
    if tree_a.feature != tree_b.feature:
        return False
    if not np.isclose(tree_a.threshold, tree_b.threshold):
        return False
    return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: Tuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """
    if tuple_action:
        cont_action = all((isinstance(sp, Box) for sp in env.action_space.spaces))
    else:
        cont_action = True
    if tuple_obs:
        cont_obs = all((isinstance(sp, Box) for sp in env.observation_space.spaces))
    else:
        cont_obs = True
    return (cont_action, cont_obs)

