def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """
    Compute the Radial Basis Function (RBF) kernel matrix.

    The function follows the specifications given in the task description.
    It returns -1 on invalid input, otherwise a nested list containing the kernel matrix rounded to four decimal places.

    Args:
        X (list[list[int | float]]): The input feature matrix.
        Y (list[list[int | float]] | None, optional): The target matrix. Defaults to None.
        sigma (float | list[float] | None, optional): The scale parameter. Defaults to None.

    Returns:
        list[list[float]]: The kernel matrix.
    """
    if sigma is None:
        if len(X) != len(Y):
            return -1
        if len(X[0]) != len(Y[0]):
            return -1
        if sigma is not None and (not isinstance(sigma, list)):
            sigma = [sigma]
        if not isinstance(sigma, (int, float)):
            return -1
        if sigma <= 0:
            return -1
    if sigma is None:
        sigma = [np.sqrt(2 / len(X[0]))]
    elif isinstance(sigma, list):
        sigma = np.array(sigma)
    else:
        sigma = np.array(sigma)
    kernel_matrix = np.exp(-0.5 * np.sum((X - np.array([0])) ** 2, axis=1) / sigma ** 2)
    kernel_matrix = np.round(kernel_matrix, 4)
    return kernel_matrix.tolist()

import numpy as np
def sigmoid(z):
    """
    Compute the numerically stable, element-wise sigmoid activation.

    The function must work for a scalar, list/tuple, or NumPy ndarray input.

    Args:
        z: int, float, list, tuple, or numpy.ndarray – input values.

    Returns:
        float if *z* is a scalar, otherwise a Python list obtained with
        ``numpy.ndarray.tolist()``, where every element is the sigmoid of the
        corresponding input value and is rounded to four decimal places.
    """
    if isinstance(z, (int, float)):
        return round(np.exp(-z) / (1 + np.exp(-z)), 4)
    elif isinstance(z, (list, tuple)):
        z = np.array(z)
    elif isinstance(z, np.ndarray):
        return np.round(z * sigmoid(z), 4)
    else:
        raise ValueError('Input must be a scalar, list, tuple, or numpy.ndarray')

class Node:

    def __init__(self, label: int, x: float, s: float, y: int, child: list):
        self.label = label
        self.x = x
        self.s = s
        self.y = y
        self.child = child
def predict_sample(root: Node, features: list | tuple, task_type: str) -> int:
    """
    Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if task_type == 'classification':
        if root.y is not None:
            return root.y
        else:
            for feature in features:
                for child in root.child:
                    if child.x == feature:
                        return predict_sample(child, features, task_type)
            return None
    elif task_type == 'regression':
        if root.y is not None:
            return root.y
        else:
            for feature in features:
                for child in root.child:
                    if feature <= child.x:
                        return predict_sample(child, features, task_type)
            return None
    else:
        raise ValueError("Invalid task_type. It should be either 'classification' or 'regression'.")
