from typing import Any, List, Tuple, Union
def action_stats(env: Any, md_action: bool, cont_action: bool) -> Tuple[List[Union[int, float]], Union[List[Tuple[int, ...]], None], int]:
    """
    Summarise an RL environment's action space.

    Args:
        env: Environment instance exposing an ``action_space`` attribute mimicking OpenAI Gym.
        md_action: ``True`` if the action space is multi-dimensional.
        cont_action: ``True`` if the action space is continuous (infinite number of actions).

    Returns:
        A tuple ``(n_actions_per_dim, action_ids, action_dim)`` where
            • n_actions_per_dim: list with the number of actions in every dimension
                                  (``numpy.inf`` for continuous ones),
            • action_ids: list of all discrete actions (cartesian product) or ``None`` if
                           any dimension is continuous,
            • action_dim: number of action dimensions.
    """
    if md_action:
        if cont_action:
            return ([np.inf] * env.action_space.shape[0], None, env.action_space.shape[0])
        else:
            action_ids = list(np.product(*[range(env.action_space.spaces[i].n) for i in range(env.action_space.spaces.__len__())]))
            return ([env.action_space.spaces[i].n for i in range(env.action_space.spaces.__len__())], action_ids, env.action_space.spaces.__len__())
    elif cont_action:
        return ([np.inf], None, 1)
    else:
        return ([env.action_space.n], list(range(env.action_space.n)), 1)

import numpy as np
def leaky_relu(z, a=0.01):
    """
    Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """
    if isinstance(z, np.ndarray):
        return np.where(z >= 0, z, a * z).tolist()
    elif isinstance(z, list):
        return [leaky_relu(i, a) for i in z]
    else:
        return np.where(z >= 0, z, a * z).item()

import numpy as np
def adaboost_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_clf: int=5) -> list[int]:
    """
    Train AdaBoost with decision stumps and predict labels for X_test.

    Args:
        X_train: 2-D NumPy array of shape (m, n) containing the training features.
        y_train: 1-D NumPy array of length m with labels **-1** or **1**.
        X_test: 2-D NumPy array of shape (k, n) containing test features.
        n_clf:   Number of weak classifiers (decision stumps) to build. Must be > 0.

    Returns:
        A Python list of length k, each element being either -1 or 1, the
        predicted class for the corresponding row in `X_test`.
    """
    alpha = [0] * n_clf
    y_pred = []
    for t in range(n_clf):
        w = np.ones_like(y_train) / len(y_train)
        error = 0
        for i in range(len(y_train)):
            if y_train[i] == -1:
                error += w[i] * (1 - y_train[i])
            else:
                error += w[i] * (1 + y_train[i])
            w[i] *= np.exp(-y_train[i] * (1 - y_train[i]) / 2)
        w /= np.sum(w)
        best_stump = None
        best_error = np.inf
        for i in range(len(y_train)):
            stump_error = 0
            for j in range(len(y_train)):
                if y_train[j] == -1:
                    stump_error += w[j] * (1 - y_train[j])
                else:
                    stump_error += w[j] * (1 + y_train[j])
            if stump_error < best_error:
                best_stump = i
                best_error = stump_error
        alpha[t] = 0.5 * np.log((1 - best_error) / best_error)
        for x in X_test:
            vote = 0
            for i in range(len(y_train)):
                if y_train[i] == -1:
                    vote += alpha[t] * (1 - y_train[i])
                else:
                    vote += alpha[t] * (1 + y_train[i])
            if vote > 0:
                y_pred.append(1)
            else:
                y_pred.append(-1)
    return y_pred

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
def compare_trees(tree_a, tree_b):
    """
    Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        return np.allclose(tree_a.value, tree_b.value)
    if isinstance(tree_a, Leaf) or isinstance(tree_b, Leaf):
        return False
    if tree_a.feature != tree_b.feature or np.abs(tree_a.threshold - tree_b.threshold) > 1e-08:
        return False
    return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)

def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool) -> Tuple[bool, bool]:
    """
    Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """
    cont_action = False
    cont_obs = False
    if tuple_action:
        if isinstance(env.action_space, (Tuple, Dict)):
            for sub_space in env.action_space.spaces.values() if isinstance(env.action_space, Dict) else env.action_space.spaces:
                if not isinstance(sub_space, Box):
                    cont_action = False
                    break
            else:
                cont_action = True
    else:
        cont_action = isinstance(env.action_space, Box)
    if tuple_obs:
        if isinstance(env.observation_space, (Tuple, Dict)):
            for sub_space in env.observation_space.spaces.values() if isinstance(env.observation_space, Dict) else env.observation_space.spaces:
                if not isinstance(sub_space, Box):
                    cont_obs = False
                    break
            else:
                cont_obs = True
    else:
        cont_obs = isinstance(env.observation_space, Box)
    return (cont_action, cont_obs)

import numpy as np
def env_stats(actions: list, observations: list) -> dict:
    """
    Compute statistics for collections of actions and observations.

    The function inspects *actions* and *observations* and returns a dictionary
    containing information about dimensionality, data type (discrete or
    continuous) and the unique values appearing in every dimension.

    Parameters
    ----------
    actions : list
        A list with the actions that were taken.  Each element is either a
        scalar or an iterable of scalars (for multi-dimensional spaces).
    observations : list
        A list with the corresponding observations.  Same structural
        requirements as *actions*.

    Returns
    -------
    dict
        A dictionary with the keys described in the task description.
    """
    stats = {'tuple_actions': False, 'tuple_observations': False, 'multidim_actions': False, 'multidim_observations': False, 'continuous_actions': False, 'continuous_observations': False, 'n_actions_per_dim': [], 'action_dim': 0, 'n_obs_per_dim': [], 'obs_dim': 0, 'action_ids': [], 'obs_ids': []}
    if len(actions) != len(observations):
        raise ValueError('Actions and observations must have the same length')
    if not actions or not observations:
        raise ValueError('Actions and observations must not be empty')
    if any((isinstance(action, (list, tuple, np.ndarray)) for action in actions)):
        stats['tuple_actions'] = True
    if any((isinstance(observation, (list, tuple, np.ndarray)) for observation in observations)):
        stats['tuple_observations'] = True
    for action in actions:
        if len(action) > 1:
            stats['multidim_actions'] = True
            stats['action_dim'] = len(action)
            stats['n_actions_per_dim'].append(len(set(action)))
    for observation in observations:
        if len(observation) > 1:
            stats['multidim_observations'] = True
            stats['obs_dim'] = len(observation)
            stats['n_obs_per_dim'].append(len(set(observation)))
    for action in actions:
        if any((not isinstance(x, int) and (not isinstance(x, float)) for x in action)):
            stats['continuous_actions'] = True
    for observation in observations:
        if any((not isinstance(x, int) and (not isinstance(x, float)) for x in observation)):
            stats['continuous_observations'] = True
    for action in actions:
        stats['action_ids'].append(sorted(set(action)))
    for observation in observations:
        stats['obs_ids'].append(sorted(set(observation)))
    return stats

import numpy as np
def sigmoid(x, order: int=0):
    """
    Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    if isinstance(x, (int, float)):
        if order == 0:
            return round(1 / (1 + np.exp(-x)), 4)
        elif order == 1:
            return round(x * (1 - x), 4)
        elif order == 2:
            return round(x * (1 - x) * (1 - 2 * x), 4)
    elif order == 0:
        return np.vectorize(lambda x: 1 / (1 + np.exp(-x)))(x).tolist()
    elif order == 1:
        return np.vectorize(lambda x: x * (1 - x))(x).tolist()
    elif order == 2:
        return np.vectorize(lambda x: x * (1 - x) * (1 - 2 * x))(x).tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """
    Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    if not isinstance(X, list) or not all((isinstance(row, list) for row in X)):
        return -1
    if not all((len(row) == len(X[0]) for row in X)):
        return -1
    if Y is not None and (not isinstance(Y, list)) or not all((isinstance(row, list) for row in Y)):
        return -1
    if Y is not None and (not all((len(row) == len(X[0]) for row in Y))):
        return -1
    if sigma is None:
        sigma = np.sqrt(len(X[0]))
    elif not isinstance(sigma, (list, float)) or (isinstance(sigma, list) and len(sigma) != len(X[0])):
        return -1
    (N, M) = (len(X), len(Y) if Y is not None else len(X))
    K = np.zeros((N, M))
    for i in range(N):
        for j in range(M):
            if Y is None:
                K[i, j] = np.exp(-0.5 * np.sum((np.array(X[i]) - np.array(X[j])) ** 2) / sigma ** 2)
            else:
                K[i, j] = np.exp(-0.5 * np.sum((np.array(X[i]) - np.array(Y[j])) ** 2) / sigma[j] ** 2)
    K = [row for row in K.tolist()]
    for i in range(N):
        for j in range(M):
            K[i][j] = round(K[i][j], 4)
    return K

from collections import Counter, defaultdict
import itertools
def fp_growth(transactions, min_sup):
    """
    Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_freq = Counter((item for transaction in transactions for item in transaction))
    item_freq = {item: freq for (item, freq) in item_freq.items() if freq >= min_sup}
    item_freq = {item: freq for (item, freq) in item_freq.items() if freq > 0}
    item_freq = {item: freq for (item, freq) in item_freq.items()}
    fp_tree = defaultdict(list)
    for transaction in transactions:
        for item in transaction:
            fp_tree[item].append(transaction)
    header_table = defaultdict(list)
    for (item, transactions) in fp_tree.items():
        for transaction in transactions:
            header_table[item].append(transaction)
    result = set()
    for item in item_freq:
        result.add((item,))
    while True:
        cond_pattern_bases = []
        for (item, transactions) in fp_tree.items():
            if len(transactions) > 1:
                cond_pattern_bases.append((item,))
        cond_fp_tree = defaultdict(list)
        for cond_pattern_base in cond_pattern_bases:
            for transaction in fp_tree[cond_pattern_base[0]]:
                cond_fp_tree[cond_pattern_base[0]].append(transaction)
        for cond_pattern_base in cond_pattern_bases:
            for transaction in cond_fp_tree[cond_pattern_base[0]]:
                for item in transaction:
                    if cond_pattern_base + (item,) not in result:
                        result.add(cond_pattern_base + (item,))
        if not cond_pattern_bases:
            break
    result = sorted(result, key=lambda x: (len(x), x))
    return result

def predict_sample(root: 'Node', features: list | tuple, task_type: str) -> any:
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if isinstance(root, dict):
        if 'y' in root:
            return root['y']
        label = root['label']
        child_index = features[label]
        return predict_sample(root['child'][child_index], features, task_type)
    elif isinstance(root, Node):
        if 'y' in root:
            return root.y
        label = root.label
        value = features[label]
        if task_type == 'regression':
            if value <= root.s:
                return predict_sample(root.child[0], features, task_type)
            else:
                return predict_sample(root.child[1], features, task_type)
        else:
            for child in root.child:
                if child.x == value:
                    return predict_sample(child, features, task_type)
            return predict_sample(root.child[1], features, task_type)
