assert is_continuous(Env(Tuple([Box(-1.0,1.0,(3,)), Box(-1.0,1.0,(3,))]), Box(-1.0,1.0,(3,))), True, False) == (True, True), "TC4 failed"
assert is_continuous(Env(Tuple([Box(-1.0,1.0,(3,)), Discrete(5)]), Box(-1.0,1.0,(3,))), True, False) == (False, True), "TC5 failed"
assert is_continuous(Env(Box(-1.0,1.0,(3,)), Dict({"a": Box(-1.0,1.0,(3,)), "b": Box(-1.0,1.0,(3,))})), False, True) == (True, True), "TC6 failed"
