import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    X = np.array(X)
    if Y is not None:
        Y = np.array(Y)
        if X.shape[1] != Y.shape[1]:
            return -1
    else:
        Y = X
    C = X.shape[1]
    if sigma is None:
        sigma = np.sqrt(C / 2)
    if isinstance(sigma, float) or isinstance(sigma, int):
        if sigma <= 0:
            return -1
        sigma_array = np.array([sigma] * C)
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        sigma_array = np.array(sigma)
        if np.any(sigma_array <= 0):
            return -1
    else:
        return -1
    kernel_matrix = np.zeros((X.shape[0], Y.shape[0]))
    for i in range(X.shape[0]):
        for j in range(Y.shape[0]):
            diff = X[i] - Y[j]
            scaled_diff = diff / sigma_array
            squared_distance = np.sum(scaled_diff ** 2)
            kernel_matrix[i, j] = np.exp(-0.5 * squared_distance)
    kernel_matrix = np.round(kernel_matrix, 4).tolist()
    return kernel_matrix
