def rref(matrix):
    mat = np.array(matrix)
    (m, n) = mat.shape
    for i in range(n):
        row_with_nonzero = None
        for j in range(m):
            if mat[j, i] != 0:
                row_with_nonzero = j
                break
        if row_with_nonzero is None:
            continue
        mat[[i, row_with_nonzero]] = mat[[row_with_nonzero, i]]
        pivot_value = mat[i, i]
        mat[i] /= pivot_value
        for j in range(m):
            if j != i:
                factor = mat[j, i]
                mat[j] -= factor * mat[i]
    return mat.tolist()
